'use strict';

var util = require('util');

var Network = require('../index').Network;

function Libelium(options) {
  Network.call(this, 'libelium', options);
}

util.inherits(Libelium, Network);

Libelium.prototype.getDevice = function (addr, options, cb) {
  if (typeof options === 'function') {
    cb = options;
  }

  return cb && cb();
};

Libelium.prototype.discover = function (driverOrModel, cb) {
  return cb && cb();
};

module.exports = new Libelium();
